mapper.select:
select count(*) from tbl_activity act join tbl_user user1 on user1.id=act.owner left join tbl_user user2 on act.edit_by=user2.id
act.activity_status=0
and act.name like concat('%',#{activity.name},'%')
and user1.name like concat('%',#{activity.owner},'%')
and act.start_date>=#{activity.staractivitytDate}
and act.end_date<=#{activity.endDate}

mapper.update:
update tbl_activity
set activity_status = 1
where id in

    #{id}
;

mapper.select:
select column_name
from information_schema.COLUMNS
where TABLE_NAME = #{tableName};

select act.id, user1.name AS owner, act.name, act.start_date, act.end_date, act.cost, act.description, act.create_time, user1.name AS create_by, act.edit_time, user2.name AS edit_by from tbl_activity act join tbl_user user1 on user1.id = act.owner left join tbl_user user2 on act.edit_by = user2.id where act.id in
#{id}
order by act.create_time desc
