mapper.select:
select tr.id,tr.name,
tr.customer_id,
dv1.value as stage,
user.name as owner,
dv2.value as source,
dv3.value as type,
tr.contacts_id
from tbl_transaction tr
join tbl_customer cust on cust.id = tr.customer_id
join tbl_contacts contacts on contacts.id = tr.contacts_id
join tbl_dic_value dv1 on dv1.id = tr.stage
join tbl_dic_value dv2 on dv2.id = contacts.source
join tbl_dic_value dv3 on dv3.id = tr.type
join tbl_user user on user.id = tr.owner


        tr.name like #{map.name}


        cust.name like #{map.customerName}


        tr.stage = #{map.stage}


        tr.type = #{map.transactionType}


        tr.source = #{map.source}


        contacts.fullname = #{map.contactName}

select count(*)
from tbl_transaction tr
join tbl_customer cust on cust.id = tr.customer_id
join tbl_dic_value dv on dv.id = tr.stage
join tbl_user user on user.id = tr.owner
join tbl_contacts contacts on contacts.id = tr.contacts_id


        tr.name like #{map.name}


        cust.name like #{map.customerName}


        tr.stage = #{map.stage}


        tr.type = #{map.transactionType}


        tr.source = #{map.source}


        contacts.fullname = #{map.contactName}
